(function onLoad() {
    function setDisplaysBasedOnType(value) {
        fs.setDisplay('ref_collection', value === 'record');
    }

    setDisplaysBasedOnType(fs.getValue('type'));

    return function onChange(field, value) {
        // write your onChange code here 
        if (field.name === 'type') {
            setDisplaysBasedOnType(value);
        }
    };
})();
