(function onLoad() {
    // write your onLoad code here 
    return function onChange(field, value) {
        if (field.name === 'type') {
            if (value === 'on_load') {
                fs.setValue('script', "(function onLoad() {\n    // write your code here \n})();");
            } else if (value === 'on_change') {
                fs.setValue('script', "(function onLoad() {\n    // write your onLoad code here \n    return function onChange(field, value) {\n        // write your onChange code here \n    };\n})();");     
            } else if (value === 'on_submit') {
                fs.setValue('script', "(function onLoad() {\n    // write your onLoad code here \n    return function onSubmit(resolve, reject) {\n        resolve();\n    };\n})();",);
            }
        }
    };
})();
