records.script:
(function onLoad() {
    function setDisplaysBasedOnType(value) {
        fs.setDisplay('ref', value === 'reference');
        fs.setDisplay('length', value === 'string');
    }

    setDisplaysBasedOnType(fs.getValue('type'));

    return function onChange(field, value) {
        // write your onChange code here 
        if (field.name === 'type') {
            setDisplaysBasedOnType(value);
        }
    };
})();

(function onLoad() {
    if (fs.isEditForm()) {
        ['name', 'type'].forEach(function (fieldName) {
            fs.setMandatory(fieldName, false);
            fs.setReadOnly(fieldName, true);
        });
    }
})();
